import React, { useState } from 'react';
import { View, TextInput, Image, TouchableOpacity, SafeAreaView, FlatList, ActivityIndicator } from 'react-native';
import MiniCard from '../components/MiniCard'



const SearchScreen = () => {
    const [value, setValue] = useState("")
    const [miniCardData, setMiniCard] = useState([])
    const [loading, setLoading] = useState(false)

    const fetchData = () => {
        setLoading(true)
        fetch(`https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=10&q=${value}&type=video&key=AIzaSyA0J99dPQsGXukBnOYK3ZYoI0NLfH288C0`)
            .then(res => res.json())
            .then(data => {
                setLoading(false)
                setMiniCard(data.items)

            })
    }
    return (
        <SafeAreaView style={{ flex: 1, backgroundColor: "skyblue" }}>

            <View style={{ flex: 1, flexDirection: "column", backgroundColor: "white" }}>

                <View style={{ flex: 1 }}>
                    <View style={{
                        padding: 5,
                        flexDirection: "row",
                        height: 100,
                        justifyContent: "space-around",

                    }}>
                        <Image source={{ uri: 'data:image/png;base64,' }}
                            style={{ width: 40, height: 28, marginLeft: 10 }} />

                        <TextInput
                            style={{
                                width: "70%",
                                height: 35,
                                backgroundColor: "#e6e6e6",
                                fontSize: 20
                            }}
                            value={value}
                            onChangeText={(text) => setValue(text)}

                        />
                        <TouchableOpacity onPress={() => fetchData()}>
                            <Image source={{ uri: 'data:image/png;base64,' }}
                                style={{ width: 40, height: 28, marginLeft: 10 }} />
                        </TouchableOpacity>
                    </View>


                </View>

                {loading ?
                    <ActivityIndicator
                        style={{ marginTop: 50 }} size="large" color="red" /> : null}


                <View style={{ marginTop: 35 }}>
                    <FlatList
                        data={miniCardData}
                        renderItem={({ item }) => {
                            return <MiniCard
                                videoId={item.id.videoId}
                                title={item.snippet.title}
                                channel={item.snippet.channelTitle}
                            />
                        }}
                        keyExtractor={(item) => item.id.videoId}
                    />
                </View>
            </View>
        </SafeAreaView>
    );
}

export default SearchScreen